from dataclasses import dataclass, field
from typing import List

from dataclasses_json import dataclass_json, LetterCase

import phonepe
from phonepe.sdk.pg.common.constants.headers import SDK_TYPE
from phonepe.sdk.pg.common.events.constants import EVENT_SOURCE_TYPE
from phonepe.sdk.pg.common.events.models.base_event import BaseEvent


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class BulkEvent:
    client_version: str = field(default=SDK_TYPE + ":" + phonepe.__version__)
    source: str = field(default=EVENT_SOURCE_TYPE)
    events: List[BaseEvent] = field(default=None)
