from dataclasses import dataclass, field

from dataclasses_json import dataclass_json, LetterCase

from phonepe.sdk.pg.common.models.request.instruments.new_card_details import NewCardDetails
from phonepe.sdk.pg.common.models.request.instruments.payment_v2_instrument import PaymentV2Instrument
from phonepe.sdk.pg.payments.v2.models.request.pg_v2_instrument_type import PgV2InstrumentType


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class CardPaymentV2Instrument(PaymentV2Instrument):
    type: str = field(default=PgV2InstrumentType.CARD)
    auth_mode: str = field(default=None)
    card_details: NewCardDetails = field(default=None)
    saved_card: bool = field(default=None)
    merchant_user_id: str = field(default=None)
