from dataclasses import dataclass, field

from dataclasses_json import dataclass_json, LetterCase

from phonepe.sdk.pg.common.models.request.instruments.billing_address import BillingAddress
from phonepe.sdk.pg.common.models.request.instruments.expiry import Expiry


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class NewCardDetails:
    encrypted_card_number: str = field(default=None)
    encryption_key_id: int = field(default=None)
    card_holder_name: str = field(default=None)
    expiry: Expiry = field(default=None)
    encrypted_cvv: str = field(default=None)
    billing_address: BillingAddress = field(default=None)
