from dataclasses import dataclass, field

from dataclasses_json import dataclass_json, LetterCase

from phonepe.sdk.pg.common.models.request.instruments.collect_payment_details import CollectPaymentDetails
from phonepe.sdk.pg.common.models.request.instruments.collect_payment_details_type import \
    CollectPaymentDetailsType


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class PhoneNumberCollectPaymentDetails(CollectPaymentDetails):
    type: str = field(default=CollectPaymentDetailsType.PHONE_NUMBER)
    phone_number: str = field(default=None)
