from dataclasses import field, dataclass
from typing import List

from dataclasses_json import dataclass_json, LetterCase
from phonepe.sdk.pg.common.models.request.payment_mode_constraints.card_type import CardType
from phonepe.sdk.pg.common.models.request.payment_mode_constraints.payment_mode_constraint import PaymentModeConstraint

from phonepe.sdk.pg.common.models.request.pg_v2_instrument_type import (
    PgV2InstrumentType,
)


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class CardPaymentModeConstraint(PaymentModeConstraint):
    type: PgV2InstrumentType = field(default=PgV2InstrumentType.CARD)
    card_types: List[CardType] = field(default=None)
