from dataclasses import field, dataclass

from dataclasses_json import dataclass_json, LetterCase

from phonepe.sdk.pg.common.models.payment_flow_type import PaymentFlowType
from phonepe.sdk.pg.common.models.request.payment_flow import PaymentFlow
from phonepe.sdk.pg.subscription.v2.models.request.redemption_retry_strategy import RedemptionRetryStrategy


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class SubscriptionRedemptionPaymentFlow(PaymentFlow):
    type: PaymentFlowType = field(default=PaymentFlowType.SUBSCRIPTION_REDEMPTION)
    merchant_subscription_id: str = field(default=None)
    redemption_retry_strategy: RedemptionRetryStrategy = field(default=None)
    auto_debit : bool = field(default=None)
