from dataclasses import dataclass, field
from typing import Optional

from dataclasses_json import dataclass_json, LetterCase

from phonepe.sdk.pg.common.models.response.payment_flow_response import (
    PaymentFlowResponse,
)
from phonepe.sdk.pg.subscription.v2.models.request.redemption_retry_strategy import (
    RedemptionRetryStrategy,
)


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class SubscriptionRedemptionPaymentFlowResponse(PaymentFlowResponse):
    merchant_subscription_id: Optional[str] = field(default=None)
    redemption_retry_strategy: Optional[RedemptionRetryStrategy] = field(default=None)
    auto_debit: Optional[bool] = field(default=None)
    valid_after: Optional[int] = field(default=None)
    valid_upto: Optional[int] = field(default=None)
    notified_at: Optional[int] = field(default=None)
